'use strict';

// MODULES //

var tape = require( 'tape' );
var OnlineSGDRegression = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof OnlineSGDRegression, 'function', 'main export is a function' );
	t.end();
});

tape( 'the constructor returns a OnlineSGDRegression instance', function test( t ) {
	var model = new OnlineSGDRegression();
	t.equal( model instanceof OnlineSGDRegression, true, 'returns a OnlineSGDRegression model' );
	t.end();
});

tape( 'the constructor does not require the `new` operator', function test( t ) {
	var model;

	// When not supplying options...
	model = OnlineSGDRegression();
	t.equal( model instanceof OnlineSGDRegression, true, 'returns a OnlineSGDRegression model' );

	// When supplying an options object...
	model = OnlineSGDRegression({
		'intercept': false
	});
	t.equal( model instanceof OnlineSGDRegression, true, 'returns a OnlineSGDRegression model' );

	t.end();
});

tape( 'the constructor function throws an error if the `options` object is not a simple object', function test( t ) {
	var values;
	var model;
	var i;

	values = [
		'abc',
		5,
		null,
		true,
		undefined,
		NaN,
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			model = new OnlineSGDRegression( value );
		};
	}
});

tape( 'the `loss` option of the constructor specifies the used loss function', function test( t ) {
	var functions;
	var values;
	var model;
	var len;
	var i;

	values = [
		'epsilonInsensitive',
		'huber',
		'squaredError'
	];

	functions = [
		'epsilonInsensitiveLoss',
		'huberLoss',
		'squaredErrorLoss'
	];

	len = values.length;

	for ( i = 0; i < len; i++ ) {
		model = new OnlineSGDRegression({
			'loss': values[ i ]
		});
		t.equal( model._lossfun.name, functions[ i ], 'used loss function is ' + functions[i] );
	}

	t.end();
});

tape( 'the constructor throws an error if supplied an unknown `loss` option', function test( t ) {
	t.throws( badValue, Error, 'throws an error when provided unknown loss' );
	t.end();

	function badValue() {
		var model = new OnlineSGDRegression({
			'loss': 'hilbert'
		});
	}
});

tape( 'the `learningRate` option can be used to set the strength schedule of the gradient descent algorithm', function test( t ) {
	var models;
	var values;
	var lambda;
	var eta0;
	var eta;
	var len;
	var i;
	var j;

	values = [
		'basic',
		'constant',
		'pegasos'
	];
	len = values.length;
	models = new Array( len );
	lambda = 1e-3;
	eta0 = 0.02;

	for ( i = 0; i < len; i++ ) {
		models[ i ] = new OnlineSGDRegression({
			'learningRate': values[ i ]
		});
	}
	for ( j = 2; j < 10; j++ ) {
		for ( i = 0; i < len; i++ ) {
			models[ i ].update( [1.0,1.0,1.0], 2.0 );
			eta = models[ i ].getEta();
			switch ( values[i] ) {
			case 'basic':
				t.equal( eta, 1000.0 / ( 1000 + j ), 'learning rate is equal to 1000 / ( 1000 + it )' );
			break;
			case 'constant':
				t.equal( eta, eta0, 'learning rate is constant and equal to eta0' );
			break;
			case 'pegasos':
				t.equal( eta, 1.0 / ( lambda * j ), 'learning rate is equal to 1 / ( lambda + it )' );
			break;
			}
		}
	}
	t.end();
});

tape( 'the constructor throws an error if supplied an unknown `learningRate` option', function test( t ) {
	t.throws( badValue, Error, 'throws an error when provided unknown learning rate' );
	t.end();

	function badValue() {
		var model = new OnlineSGDRegression({
			'learningRate': 'scaling'
		});
	}
});

tape( 'the constructor has an `intercept` option determining whether the regression model should include an implicit intercept / bias term', function test( t ) {
	var model;

	model = new OnlineSGDRegression({
		'intercept': true
	});
	model.update( [1.0,1.0,1.0], 2.0 );
	t.equal( model.coefs.length, 4, 'the model contains a regression coefficient for the intercept' );

	model = new OnlineSGDRegression({
		'intercept': false
	});
	model.update( [1.0,1.0,1.0], 2.0 );
	t.equal( model.coefs.length, 3, 'the model does not contain an intercept regression coefficient' );

	t.end();
});

tape( 'the `update` method throws an error if the `x` value is not an array', function test( t ) {
	var values;
	var model;
	var i;
	var y;

	model = new OnlineSGDRegression();
	values = [
		'abc',
		5,
		null,
		true,
		undefined,
		NaN,
		{},
		function(){}
	];
	y = 2.0;

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function() {
			model.update( value, y );
		};
	}
});

tape( 'the `update` method throws an error if the `x` is an array with an invalid number of elements', function test( t ) {
	var model;
	var i;
	var y;
	var x;

	model = new OnlineSGDRegression();
	y = 2.0;
	x = [1.0,1.0];
	model.update( x, y );

	for ( i = 0; i < 10; i++ ) {
		x.push( 1.0 );
		t.throws( badValue, TypeError, 'throws an error when provided an array of any length other than two' );
	}
	t.end();

	function badValue() {
		model.update( x, y );
	}
});

tape( 'the `predict` method calculates the inner product of the weights and feature vector `x`', function test( t ) {
	var expected;
	var weights;
	var actual;
	var model;
	var i;
	var x;

	model = new OnlineSGDRegression();

	model.update( [0.0,0.0], 2.0 );
	weights = model.coefs;

	x = [ 2.0, 2.0 ];
	actual = model.predict( x );
	expected = 0;
	for ( i = 0; i < weights.length; i++ ) {
		expected += x[ i ] * weights[ i ];
	}

	t.end();
});


