'use strict';

/**
* Computes an element-wise multiplication of a numeric array by a scalar.
*
* @private
* @param {NumericArray} y - destination array
* @param {NumericArray} x - input array
* @param {number} scalar - scalar
* @returns {NumericArray} destination array
*/
function mult( y, x, scalar ) {
	var i;
	for ( i = 0; i < x.length; i++ ) {
		y[ i ] = x[ i ] * scalar;
	}
	return y;
} // end FUNCTION mult()


// EXPORTS //

module.exports = mult;
