'use strict';

/**
* Compute a product incrementally.
*
* @module @stdlib/math/statistics/incr/prod
*
* @example
* var incrprod = require( '@stdlib/math/statistics/incr/prod' );
*
* var accumulator = incrprod();
*
* var prod = accumulator();
* // returns 1.0
*
* prod = accumulator( 2.0 );
* // returns 2.0
*
* prod = accumulator( -5.0 );
* // returns -10.0
*
* prod = accumulator();
* // returns -10.0
*/

// MODULES //

var incrprod = require( './incrprod.js' );


// EXPORTS //

module.exports = incrprod;
