'use strict';

/**
* Compute a range incrementally.
*
* @module @stdlib/math/statistics/incr/range
*
* @example
* var incrrange = require( '@stdlib/math/statistics/incr/range' );
*
* var accumulator = incrrange();
*
* var range = accumulator();
* // returns 0.0
*
* range = accumulator( 3.14 );
* // returns 0.0
*
* range = accumulator( -5.0 );
* // returns 8.14
*
* range = accumulator( 10.1 );
* // returns 15.1
*
* range = accumulator();
* // returns 15.1
*/

// MODULES //

var incrrange = require( './incrrange.js' );


// EXPORTS //

module.exports = incrrange;
