'use strict';

/**
* Compute an arithmetic mean incrementally.
*
* @module @stdlib/math/statistics/incrmean
*
* @example
* var incrmean = require( '@stdlib/math/statistics/incrmean' );
*
* var accumulator = incrmean();
*
* var mu = accumulator();
* // returns 0.0
*
* mu = accumulator( 2.0 );
* // returns 2.0
*
* mu = accumulator( -5.0 );
* // returns -1.5
*
* mu = accumulator();
* // returns -1.5
*/

// MODULES //

var incrmean = require( './incrmean.js' );


// EXPORTS //

module.exports = incrmean;
