'use strict';

/**
* Compute a minimum value incrementally.
*
* @module @stdlib/math/statistics/incrmin
*
* @example
* var incrmin = require( '@stdlib/math/statistics/incrmin' );
*
* var accumulator = incrmin();
*
* var min = accumulator();
* // returns null
*
* min = accumulator( 3.14 );
* // returns 3.14
*
* min = accumulator( -5.0 );
* // returns -5.0
*
* min = accumulator( 10.1 );
* // returns -5.0
*
* min = accumulator();
* // returns -5.0
*/

// MODULES //

var incrmin = require( './incrmin.js' );


// EXPORTS //

module.exports = incrmin;
