'use strict';

// MODULES //

var tape = require( 'tape' );
var incrmin = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof incrmin, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an accumulator function', function test( t ) {
	t.equal( typeof incrmin(), 'function', 'returns a function' );
	t.end();
});

tape( 'if not provided any values, the initial returned minimum value is `null`', function test( t ) {
	var acc = incrmin();
	t.equal( acc(), null, 'returns null' );
	t.end();
});

tape( 'the accumulator function incrementally computes a minimum value', function test( t ) {
	var expected;
	var actual;
	var data;
	var acc;
	var min;
	var N;
	var d;
	var i;

	data = [ 2.0, 3.0, 1.0, 4.0, 3.0, 4.0 ];
	N = data.length;

	expected = new Array( N );
	actual = new Array( N );

	acc = incrmin();

	min = data[ 0 ];
	for ( i = 0; i < N; i++ ) {
		d = data[ i ];
		if ( d < min ) {
			min = d;
		}
		expected[ i ] = min;
		actual[ i ] = acc( d );
	}
	t.deepEqual( actual, expected, 'returns expected incremental results' );
	t.end();
});

tape( 'if not provided an input value, the accumulator function returns the current minimum value', function test( t ) {
	var data;
	var acc;
	var i;

	data = [ 2.0, 1.0, 3.0 ];
	acc = incrmin();
	for ( i = 0; i < data.length; i++ ) {
		acc( data[ i ] );
	}
	t.equal( acc(), 1.0, 'returns the current accumulated minimum value' );
	t.end();
});
