'use strict';

/**
* Compute a moving sum incrementally.
*
* @module @stdlib/math/statistics/incrmsum
*
* @example
* var incrmsum = require( '@stdlib/math/statistics/incrmsum' );
*
* var accumulator = incrmsum( 3 );
*
* var sum = accumulator();
* // returns null
*
* sum = accumulator( 2.0 );
* // returns 2.0
*
* sum = accumulator( -5.0 );
* // returns -3.0
*
* sum = accumulator( 3.0 );
* // returns 0.0
*
* sum = accumulator( 5.0 );
* // returns 3.0
*
* sum = accumulator();
* // returns 3.0
*/

// MODULES //

var incrmsum = require( './incrmsum.js' );


// EXPORTS //

module.exports = incrmsum;
