'use strict';

/**
* Compute a sample skewness incrementally.
*
* @module @stdlib/math/statistics/incrskewness
*
* @example
* var incrskewness = require( '@stdlib/math/statistics/incrskewness' );
*
* var accumulator = incrskewness();
*
* var skewness = accumulator();
* // returns null
*
* skewness = accumulator( 2.0 );
* // returns null
*
* skewness = accumulator( -5.0 );
* // returns null
*
* skewness = accumulator( -10.0 );
* // returns ~0.492
*
* skewness = accumulator();
* // returns ~0.492
*/

// MODULES //

var incrskewness = require( './incrskewness.js' );


// EXPORTS //

module.exports = incrskewness;
