'use strict';

/**
* Compute a one-sample Student's t-Test.
*
* @module @stdlib/math/statistics/ttest
*
* @example
* var ttest = require( '@stdlib/math/statistics/ttest' );
* var normal = require( '@stdlib/math/base/random/normal' ).factory;
*
* var rnorm;
* var out;
* var i;
* var x;
* var y;
*
* rnorm = normal( 1.0, 2.0, {
*     'seed': 786
* });
*
* // One-sample t-test:
* x = new Array( 100 );
* for ( i = 0; i < x.length; i++ ) {
*     x[ i ] = rnorm();
* }
* out = ttest( x );
*
* // Paired t-test:
* x = new Array( 100 );
* y = new Array( 100 );
* for ( i = 0; i < x.length; i++ ) {
*     x[ i ] = rnorm();
*     y[ i ] = rnorm();
* }
* out = ttest( x, y );
*/

// MODULES //

var ttest = require( './ttest.js' );


// EXPORTS //

module.exports = ttest;
