'use strict';

/**
* Returns the unique elements in an array.
*
* @private
* @param {NumericArray} L - input array
* @returns {Array} array of unique elements
*/
function unique( L ) {
	var out;
	var i;

	out = [];

	// Base case, only one element to save work
	if ( L.length === 1 ) {
		return L;
	}
	for ( i = 0; i < L.length; i++ ) {
		if ( !out.includes(L[i]) ) {
			// We have a unique element:
			out.push(L[i]);
		}
	}
	return out;
}


// EXPORTS //

module.exports = unique;
