'use strict';

/**
* Computes the number of unique elements in an array.
*
* @private
* @param {NumericArray} L - input array
* @returns {Array} Array of unique elements
*/
function uniqueElems(L) {
	var unique;
	var i;

	unique = [];

	// Base case, only one element to save work
	if ( L.length === 1 ) {
		return L;
	}

	for ( i = 0; i < L.length; i++ ) {
		if (!unique.includes(L[i]) ) {
			// We have a unique element
			unique.push(L[i]);
		}
	}
	return unique;
}


// EXPORTS //

module.exports = uniqueElems;
