'use strict';

/**
* Computes an element-wise division of a numeric array by a scalar.
*
* @private
* @param {NumericArray} y - destination array
* @param {NumericArray} x - input array
* @param {number} scalar - divisor
* @returns {NumericArray} destination array
*/
function div( y, x, scalar ) {
	var i;
	for ( i = 0; i < x.length; i++ ) {
		y[ i ] = x[ i ] / scalar;
	}
	return y;
}


// EXPORTS //

module.exports = div;
