'use strict';

// MAIN //

/**
* Creates a frequency table of data in an array.
*
* @private
* @param {IntegerArray} arr - input array
* @returns {IntegerArray} frequency table
*
* @example
* var arr = [ 0, 1, 1, 0, 0, 2, 3, 5 ];
* var freq = tabulate( arr );
* // returns [ 3, 2, 1, 1, 0, 1 ]
*/
function tabulate( arr ) {
	var freq = [];
	var len;
	var i;
	var k;
	var x;

	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		x = arr[ i ];
		if ( freq.length < x+1 ) {
			for ( k = freq.length; k < x+1; k++ ) {
				freq.push( 0 );
			}
		}
		freq[ x ] += 1;
	}
	return freq;
}


// EXPORTS //

module.exports = tabulate;
