'use strict';

/**
* Compute a moving statistical summary incrementally.
*
* @module @stdlib/math/stats/incr/msummary
*
* @example
* var incrmsummary = require( '@stdlib/math/stats/incr/msummary' );
*
* var accumulator = incrmsummary( 3 );
*
* var summary = accumulator();
* // returns {}
*
* summary = accumulator( 2.0 );
* // returns {...}
*
* summary = accumulator( -5.0 );
* // returns {...}
*
* summary = accumulator();
* // returns {...}
*/

// MODULES //

var incrmsummary = require( './incrmsummary.js' );


// EXPORTS //

module.exports = incrmsummary;
