'use strict';

/**
* Compute a moving unbiased sample variance incrementally.
*
* @module @stdlib/math/stats/incr/mvariance
*
* @example
* var incrmvariance = require( '@stdlib/math/stats/incr/mvariance' );
*
* var accumulator = incrmvariance( 3 );
*
* var s2 = accumulator();
* // returns null
*
* s2 = accumulator( 2.0 );
* // returns 0.0
*
* s2 = accumulator( -5.0 );
* // returns 24.5
*
* s2 = accumulator( 3.0 );
* // returns 19.0
*
* s2 = accumulator( 5.0 );
* // returns 28.0
*
* s2 = accumulator();
* // returns 28.0
*/

// MODULES //

var incrmvariance = require( './incrmvariance.js' );


// EXPORTS //

module.exports = incrmvariance;
