'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var incrrange = require( './../lib' );

var accumulator;
var range;
var v;
var i;

// Initialize an accumulator:
accumulator = incrrange();

// For each simulated datum, update the range...
console.log( '\nValue\tRange\n' );
for ( i = 0; i < 100; i++ ) {
	v = randu() * 100.0;
	range = accumulator( v );
	console.log( '%d\t%d', v.toFixed( 4 ), range.toFixed( 4 ) );
}
console.log( '\nFinal range: %d\n', accumulator() );
