'use strict';

var randu = require( '@stdlib/random/base/randu' );
var incrsum = require( './../lib' );

var accumulator;
var sum;
var v;
var i;

// Initialize an accumulator:
accumulator = incrsum();

// For each simulated datum, update the sum...
console.log( '\nValue\tSum\n' );
for ( i = 0; i < 100; i++ ) {
	v = randu() * 100.0;
	sum = accumulator( v );
	console.log( '%d\t%d', v.toFixed( 4 ), sum.toFixed( 4 ) );
}
console.log( '\nFinal sum: %d\n', accumulator() );
