'use strict';

// MODULES //

var incrcount = require( '@stdlib/math/stats/incr/count' );
var incrmax = require( '@stdlib/math/stats/incr/max' );
var incrmin = require( '@stdlib/math/stats/incr/min' );
var incrrange = require( '@stdlib/math/stats/incr/range' );
var incrmidrange = require( '@stdlib/math/stats/incr/midrange' );
var incrsum = require( '@stdlib/math/stats/incr/sum' );
var incrmean = require( '@stdlib/math/stats/incr/mean' );
var incrvariance = require( '@stdlib/math/stats/incr/variance' );
var incrstdev = require( '@stdlib/math/stats/incr/stdev' );


// MAIN //

/**
* Returns an accumulator function which incrementally computes a statistical summary.
*
* @returns {Function} accumulator function
*
* @example
* var accumulator = incrsummary();
*/
function incrsummary() {
	var midrange = incrmidrange();
	var variance = incrvariance();
	var count = incrcount();
	var range = incrrange();
	var stdev = incrstdev();
	var mean = incrmean();
	var max = incrmax();
	var min = incrmin();
	var sum = incrsum();
	var summary = {};

	return accumulator;

	/**
	* If provided a value, the accumulator function returns an updated summary. If not provided a value, the accumulator function returns the current summary.
	*
	* @private
	* @param {number} [x] - new value
	* @returns {Object} summary
	*
	* @example
	* var summary = accumulator();
	* // returns {}
	*
	* summary = accumulator( 2.0 );
	* // returns {...}
	*
	* summary = accumulator( -5.0 );
	* // returns {...}
	*
	* summary = accumulator();
	* // returns {...}
	*/
	function accumulator( x ) {
		if ( arguments.length === 0 ) {
			return summary;
		}
		summary.count = count( x );
		summary.max = max( x );
		summary.min = min( x );
		summary.range = range( x );
		summary.midrange = midrange( x );
		summary.sum = sum( x );
		summary.mean = mean( x );
		summary.variance = variance( x );
		summary.stdev = stdev( x );
		return summary;
	}
}


// EXPORTS //

module.exports = incrsummary;
