'use strict';

/**
* Compute an unbiased sample variance incrementally.
*
* @module @stdlib/math/stats/incr/variance
*
* @example
* var incrvariance = require( '@stdlib/math/stats/incr/variance' );
*
* var accumulator = incrvariance();
*
* var s2 = accumulator();
* // returns 0.0
*
* s2 = accumulator( 2.0 );
* // returns 0.0
*
* s2 = accumulator( -5.0 );
* // returns 24.5
*
* s2 = accumulator();
* // returns 24.5
*/

// MODULES //

var incrvariance = require( './incrvariance.js' );


// EXPORTS //

module.exports = incrvariance;
