'use strict';

// MODULES //

var sqrt = require( '@stdlib/math/base/special/sqrt' );


// MAIN //

/**
* Computes the unbiased standard deviation of an array.
*
* @private
* @param {NumericArray} arr - input array
* @returns {number} standard deviation
*
* @example
* var arr = [ 2, 3, 4, 3, 5 ];
* var out = stdev( arr );
* // returns ~1.14
*/
function stdev( arr ) {
	var delta;
	var mean;
	var len;
	var M2;
	var i;
	var x;

	delta = 0.0;
	mean = 0.0;
	M2 = 0.0;
	len = arr.length;
	for ( i = 0; i < len; i++ ) {
		x = arr[ i ];
		delta = x - mean;
		mean += delta / (i+1);
		M2 += delta * ( x - mean );
	}
	return sqrt( M2 / ( i - 1 ) );
} // end FUNCTION stdev()


// EXPORTS //

module.exports = stdev;
