'use strict';

/**
* Compute a one-sample z-Test.
*
* @module @stdlib/math/stats/ztest
*
* @example
* var ztest = require( '@stdlib/math/stats/ztest' );
* var normal = require( '@stdlib/math/base/random/normal' ).factory;
*
* var rnorm;
* var out;
* var i;
* var x;
* var y;
*
* rnorm = normal( 1.0, 2.0, {
*     'seed': 786
* });
*
* // One-sample z-test:
* x = new Array( 300 );
* for ( i = 0; i < x.length; i++ ) {
*     x[ i ] = rnorm();
* }
* out = ztest( x, 2.0 );
*/

// MODULES //

var ztest = require( './ztest.js' );


// EXPORTS //

module.exports = ztest;
