'use strict';

var rnorm = require( '@stdlib/random/base/normal' );
var ztest2 = require( './../lib' );

var table;
var out;
var x;
var y;
var i;

// Values drawn from a Normal(4,2) distribution
x = new Array( 100 );
for ( i = 0; i < 100; i++ ) {
	x[ i ] = rnorm( 4.0, 2.0 );
}
// Values drawn from a Normal(3,2) distribution
y = new Array( 80 );
for ( i = 0; i < 80; i++ ) {
	y[ i ] = rnorm( 3.0, 2.0 );
}

out = ztest2( x, y, 2.0, 2.0 );
table = out.print();
console.log( table );


out = ztest2( x, y, 2.0, 2.0, {
	'difference': 1.0
});
table = out.print();
console.log( table );
