'use strict';

// MAIN //

/**
* Copies matrix elements to another matrix.
*
* @private
* @param {ndarray} out - destination matrix
* @param {ndarray} src - source matrix
* @returns {ndarray} destination matrix
*/
function copyMatrix( out, src ) {
	var M;
	var N;
	var i;
	var j;

	M = src.shape[ 0 ];
	N = src.shape[ 1 ];
	for ( i = 0; i < M; i++ ) {
		for ( j = 0; j < N; j++ ) {
			out.set( i, j, src.get( i, j ) );
		}
	}
	return out;
}


// EXPORTS //

module.exports = copyMatrix;
