'use strict';

// MODULES //

var sqrt = require( '@stdlib/math/base/special/sqrt' );


// MAIN //

/**
* Computes the correlation distance between two vectors.
*
* @private
* @param {ndarray} X - matrix containing the first vector
* @param {NonNegativeInteger} xi - vector row index
* @param {ArrayLikeObject} mx - column means
* @param {ndarray} Y - matrix containing the second vector
* @param {NonNegativeInteger} yi - vector row index
* @param {ArrayLikeObject} my - column means
* @returns {number} correlation distance
*/
function correlation( X, xi, mx, Y, yi, my ) {
	var dx;
	var dy;
	var xx;
	var yy;
	var xy;
	var N;
	var j;

	N = X.shape[ 1 ]; // number of columns

	xx = 0.0;
	yy = 0.0;
	xy = 0.0;
	for ( j = 0; j < N; j++ ) {
		dx = X.get( xi, j ) - mx[ j ];
		dy = Y.get( yi, j ) - my[ j ];
		xx += dx * dx;
		yy += dy * dy;
		xy += dx * dy;
	}
	return 1.0 - ( xy / ( sqrt( xx )*sqrt( yy ) ) );
}


// EXPORTS //

module.exports = correlation;
