'use strict';

// MAIN //

/**
* Computes the dot product of two vectors.
*
* @private
* @param {ndarray} X - matrix containing the first vector
* @param {NonNegativeInteger} xi - vector row index
* @param {ndarray} Y - matrix containing the second vector
* @param {NonNegativeInteger} yi - vector row index
* @returns {number} dot product
*/
function dot( X, xi, Y, yi ) { // TODO: eventually remove this function once project has implemented comparable functionality as a standalone package
	var N;
	var s;
	var j;

	N = X.shape[ 1 ]; // number of columns

	s = 0.0;
	for ( j = 0; j < N; j++ ) {
		s += X.get( xi, j ) * Y.get( yi, j );
	}
	return s;
}


// EXPORTS //

module.exports = dot;
