'use strict';

// MODULES //

var tape = require( 'tape' );
var dot = require( './../lib/dot.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof dot, 'function', 'main export is a function' );
	t.end();
});

tape( 'function calculates the dot product of two arrays', function test( t ) {
	var expected;
	var actual;
	var x;
	var y;

	x = [ 2, 3, 1 ];
	y = [ 1, 2, 3 ];
	expected = (2*1) + (3*2) + (1*3);
	actual = dot( x, y );

	t.equal( actual, expected, 'returns the dot product' );
	t.end();
});
