'use strict';

// MODULES //

var ALIASES = require( './../data/data.json' );


// MAIN //

/**
* Returns a list of standard library aliases.
*
* @returns {StringArray} list of aliases
*
* @example
* var list = aliases();
* // returns [...]
*/
function aliases() {
	return ALIASES.slice();
}


// EXPORTS //

module.exports = aliases;
