'use strict';

// MODULES //

var merge = require( '@stdlib/utils/merge' );
var MATH_CONSTANTS = require( '@stdlib/math/constants' );
var TIME_CONSTANTS = require( '@stdlib/time/constants' );


// MAIN //

/**
* Assigns values to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	merge( ns, MATH_CONSTANTS );
	merge( ns, TIME_CONSTANTS );
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
