'use strict';

var NS = {};


// MODULES //

var getKeys = require( 'object-keys' ).shim();
NS.FEMALE_FIRST_NAMES_EN = require( '@stdlib/datasets/female-first-names-en' );
NS.LIU_NEGATIVE_OPINION_WORDS = require( '@stdlib/datasets/liu-negative-opinion-words-en' );
NS.LIU_POSITIVE_OPINION_WORDS = require( '@stdlib/datasets/liu-positive-opinion-words-en' );
NS.MALE_FIRST_NAMES_EN = require( '@stdlib/datasets/male-first-names-en' );
NS.NIGHTINGALES_ROSE = require( '@stdlib/datasets/nightingales-rose' );


// VARIABLES //

var KEYS = getKeys( NS );


// ASSIGN //

/**
* Assigns modules to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	var key;
	var i;
	for ( i = 0; i < KEYS.length; i++ ) {
		key = KEYS[ i ];
		ns[ key ] = NS[ key ];
	}
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
