'use strict';

/**
* Extend a provided namespace.
*
* @module @stdlib/namespace
*
* @example
* var namespace = require( '@stdlib/namespace' );
*
* var obj = namespace( {} );
* // returns <Object>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var namespace = require( './ns.js' );
var examples = require( './examples.js' );
var help = require( './help.js' );


// MAIN //

setReadOnly( namespace, 'examples', examples );
setReadOnly( namespace, 'help', help );


// EXPORTS //

module.exports = namespace;
