'use strict';

var NS = {};

// MODULES //

var getKeys = require( 'object-keys' ).shim();


// VARIABLES //

var DISTRIBUTIONS = [
	'degenerate',
	'exponential',
	'logistic',
	'normal',
	'weibull'
];
var FUNCTIONS = [
	'cdf',
	'pdf',
	'quantile'
];
var NS = getDistributions( DISTRIBUTIONS, FUNCTIONS );
var KEYS = getKeys( NS );

// GET DISTRIBUTIONS //

/**
* Get distribution namespace.
*
* @private
* @param {Array} dists - distribution names
* @param {Array} funcs - function names
* @returns {Object} distribution namespace
*
* @example
* var NS = getDistributions();
* // NS => {...}
*/
function getDistributions( dists, funcs ) {
	var dist;
	var fun;
	var ns;
	var i;
	var j;

	ns = {};
	for ( i = 0; i < dists.length; i++ ) {
		dist = dists[ i ];
		ns[ dist ] = {};
		for ( j = 0; j < funcs.length; j++ ) {
			fun = funcs[ j ];
			ns[ dist ][ fun ] =  require( '@stdlib/math/base/dist/' + dist + '/' + fun );
		}
	}
	return ns;
} // end FUNCTION getDistributions()


// ASSIGN //

/**
* Assigns values to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	var key;
	var i;
	for ( i = 0; i < KEYS.length; i++ ) {
		key = KEYS[ i ];
		ns[ key ] = NS[ key ];
	}
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
