'use strict';

// MODULES //

var RANDOM = require( '@stdlib/math/base/random' );


// ASSIGN //

/**
* Assigns `random` object to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	ns.random = RANDOM;
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
