'use strict';

var NS = {};


// MODULES //

var getKeys = require( 'object-keys' ).shim();
NS.incrcount = require( '@stdlib/math/generics/statistics/incrcount' );
NS.incrkurtosis = require( '@stdlib/math/generics/statistics/incrkurtosis' );
NS.incrmax = require( '@stdlib/math/generics/statistics/incrmax' );
NS.incrmean = require( '@stdlib/math/generics/statistics/incrmean' );
NS.incrmidrange = require( '@stdlib/math/generics/statistics/incrmidrange' );
NS.incrmin = require( '@stdlib/math/generics/statistics/incrmin' );
NS.incrmmean = require( '@stdlib/math/generics/statistics/incrmmean' );
NS.incrmstdev = require( '@stdlib/math/generics/statistics/incrmstdev' );
NS.incrmsum = require( '@stdlib/math/generics/statistics/incrmsum' );
NS.incrmsummary = require( '@stdlib/math/generics/statistics/incrmsummary' );
NS.incrmvariance = require( '@stdlib/math/generics/statistics/incrmvariance' );
NS.incrrange = require( '@stdlib/math/generics/statistics/incrrange' );
NS.incrskewness = require( '@stdlib/math/generics/statistics/incrskewness' );
NS.incrstdev = require( '@stdlib/math/generics/statistics/incrstdev' );
NS.incrsum = require( '@stdlib/math/generics/statistics/incrsum' );
NS.incrsummary = require( '@stdlib/math/generics/statistics/incrsummary' );
NS.incrvariance = require( '@stdlib/math/generics/statistics/incrvariance' );

NS.kstest = require( '@stdlib/math/generics/statistics/kstest' );


// VARIABLES //

var KEYS = getKeys( NS );


// ASSIGN //

/**
* Assigns values to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	var key;
	var i;
	for ( i = 0; i < KEYS.length; i++ ) {
		key = KEYS[ i ];
		ns[ key ] = NS[ key ];
	}
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
