'use strict';

// MODULES //

var merge = require( '@stdlib/utils/merge' );
var NS = require( '@stdlib/math/statistics' );


// MAIN //

/**
* Assigns values to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	merge( ns, NS );
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
