'use strict';

// MODULES //

var merge = require( '@stdlib/utils/merge' );
var NS = require( '@stdlib/streams' );


// ASSIGN //

/**
* Assigns modules to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	return merge( ns, NS.math, NS.utils );
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
