'use strict';

// MODULES //

var omit = require( '@stdlib/utils/omit' );
var merge = require( '@stdlib/utils/merge' );
var NS = require( '@stdlib/utils' );


// MAIN //

/**
* Assigns modules to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	var obj = omit( NS, 'ENV' );

	// Note: we have to treat `ENV` differently, as we do NOT want to copy the object. If we did, then modifications to `ENV` would not update actual environment variables.
	ns = merge( ns, obj );
	ns.ENV = NS.ENV;

	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
