'use strict';

// MODULES //

var tape = require( 'tape' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isPlainObject = require( '@stdlib/assert/is-plain-object' );
var isPlainObjectArray = require( '@stdlib/assert/is-plain-object-array' );
var namespace = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof namespace, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the standard library namespace', function test( t ) {
	var ns1;
	var ns2;

	ns1 = namespace();
	t.strictEqual( isPlainObjectArray( ns1 ), true, 'returns an object array' );

	ns2 = namespace();
	t.strictEqual( isPlainObjectArray( ns2 ), true, 'returns an object array' );

	t.notEqual( ns1, ns2, 'returns new reference' );
	t.notEqual( ns1[ 0 ], ns2[ 0 ], 'contains different references' );
	t.notEqual( ns1[ 0 ].related, ns2[ 0 ].related, 'contains different references' );

	t.end();
});

tape( 'attached to the main export is a method to return REPL help text', function test( t ) {
	var o1;
	var o2;

	t.strictEqual( hasOwnProp( namespace, 'help' ), true, 'has property' );
	t.strictEqual( typeof namespace.help, 'function', 'has method' );

	o1 = namespace.help();
	t.strictEqual( isPlainObject( o1 ), true, 'returns an object' );

	o2 = namespace.help();
	t.strictEqual( isPlainObject( o2 ), true, 'returns an object' );
	t.notEqual( o1, o2, 'returns new reference' );

	t.end();
});

tape( 'attached to the main export is a method to return REPL examples', function test( t ) {
	var o1;
	var o2;

	t.strictEqual( hasOwnProp( namespace, 'examples' ), true, 'has property' );
	t.strictEqual( typeof namespace.examples, 'function', 'has method' );

	o1 = namespace.examples();
	t.strictEqual( isPlainObject( o1 ), true, 'returns an object' );

	o2 = namespace.examples();
	t.strictEqual( isPlainObject( o2 ), true, 'returns an object' );
	t.notEqual( o1, o2, 'returns new reference' );

	t.end();
});

