'use strict';

// MODULES //

var tape = require( 'tape' );
var namespace = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof namespace, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the input namespace object', function test( t ) {
	var obj;
	var ns;

	obj = {};
	ns = namespace( obj );

	t.equal( ns, obj, 'returns input object' );
	t.equal( Object.keys( ns ).length > 0, true, 'assigns values to namespace' );

	t.end();
});
