'use strict';

// MODULES //

var tape = require( 'tape' );
var isSingleSegmentCompatible = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isSingleSegmentCompatible, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if an array is compatible with a single memory segment', function test( t ) {
	var strides;
	var offset;
	var shape;
	var bool;

	shape = [ 3, 2 ];

	strides = [ 2, 1 ];
	offset = 0;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ 2, 1 ];
	offset = 99999;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ -2, 1 ];
	offset = 4;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ 2, -1 ];
	offset = 1;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ -2, -1 ];
	offset = 5;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ 1, 3 ];
	offset = 0;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ -1, 3 ];
	offset = 2;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ 1, -3 ];
	offset = 3;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	strides = [ -1, -3 ];
	offset = 5;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	shape = [ 1, 1, 1, 2 ];
	strides = [ 2, 2, 2, 1 ];
	offset = 0;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	shape = [ 2, 3, 10 ];
	strides = [ 30, 10, 1 ];
	offset = 99999;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	shape = [ 2, 3, 10 ];
	strides = [ 30, -10, 1 ];
	offset = 20;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, true, 'returns expected value' );

	t.end();
});

tape( 'the function returns `false` if an array is incompatible with a single memory segment', function test( t ) {
	var strides;
	var offset;
	var shape;
	var bool;

	shape = [ 1, 1, 1, 2 ];
	strides = [ 2, 2, 2, 2 ];
	offset = 0;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, false, 'returns expected value' );

	shape = [ 10 ];
	strides = [ 3 ];
	offset = 0;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, false, 'returns expected value' );

	shape = [ 2, 2 ];
	strides = [ 2, 2 ];
	offset = 0;
	bool = isSingleSegmentCompatible( shape, strides, offset );
	t.strictEqual( bool, false, 'returns expected value' );

	t.end();
});

tape( 'the function returns `false` if an array has 0 elements', function test( t ) {
	var bool = isSingleSegmentCompatible( [ 2, 0 ], [ 2, 0 ], 0 );
	t.strictEqual( bool, false, 'returns expected value' );
	t.end();
});
