'use strict';

var bytesPerElement = require( './../lib' );

var dtypes;
var nbytes;
var i;

dtypes = [
	'float64',
	'float32',
	'int8',
	'uint8',
	'uint8c',
	'int16',
	'uint16',
	'int32',
	'uint32',
	'binary',
	'generic',
	'foobar'
];

for ( i = 0; i < dtypes.length; i++ ) {
	nbytes = bytesPerElement( dtypes[ i ] );
	nbytes = ( nbytes ) ? nbytes+' bytes' : 'null';
	console.log( '%s => %s', dtypes[ i ], nbytes );
}
