#include "stdlib/ndarray/base/unary/internal/is_same_shape.h"
#include "stdlib/ndarray/base/iteration_order.h"
#include "stdlib/ndarray/ctor.h"
#include <stdint.h>

/**
* Tests whether an ndarray `x2` has the same dimensions as a specified ndarray `x1`.
*
* ## Notes
*
* -   If two ndarrays have the same dimensions, the function returns `1`; otherwise, the function returns `0`.
*
* @private
* @param x1   first ndarray
* @param x2   second ndarray
* @returns    value indicating if two ndarrays have the same dimensions
*/
int8_t stdlib_ndarray_base_unary_internal_is_same_shape( const struct ndarray *x1, const struct ndarray *x2 ) {
	int64_t ndims = stdlib_ndarray_ndims( x1 );
	if ( ndims != stdlib_ndarray_ndims( x2 ) ) {
		return 0;
	}
	int64_t *sh1 = stdlib_ndarray_shape( x1 );
	int64_t *sh2 = stdlib_ndarray_shape( x2 );
	for ( int64_t i = 0; i < ndims; i++ ) {
		if ( sh1[ i ] != sh2[ i ] ) {
			return 0;
		}
	}
	return 1;
}
