'use strict';

// MODULES //

var ORDERS = require( './orders.json' );


// MAIN //

/**
* Returns a list of ndarray orders.
*
* @returns {StringArray} list of ndarray orders
*
* @example
* var list = orders();
* // returns [ 'row-major', 'column-major' ]
*/
function orders() {
	return ORDERS.slice();
} // end FUNCTION orders()


// EXPORTS //

module.exports = orders;
