'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var MAX_UINT32 = require( '@stdlib/constants/math/uint32-max' );
var fromWordf = require( './../lib' );

var word;
var f32;
var i;

// Create single-precision floating-point numbers from unsigned integers...
for ( i = 0; i < 1000; i++ ) {
	word = round( randu()*MAX_UINT32 );
	f32 = fromWordf( word );
	console.log( 'word: %d => float32: %d', word, f32 );
}
