'use strict';

var toFloat32 = require( '@stdlib/number/float64/base/to-float32' );
var randu = require( '@stdlib/random/base/randu' );
var signbitf = require( './../lib' );

var sign;
var x;
var i;

for ( i = 0; i < 100; i++ ) {
	x = (randu()*100.0) - 50.0;
	x = toFloat32( x );
	sign = signbitf( x );
	sign = ( sign ) ? 'true' : 'false';
	console.log( 'x: %d. signbit: %s.', x, sign );
}
