// MODULES //

var debug = require( 'debug' )( 'axis:main' );
var EventEmitter = require( 'events' ).EventEmitter;
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var copy = require( '@stdlib/utils/copy' );
var createEngine = require( './engines' );
var defaults = require( './defaults.json' );
var validate = require( './validate.js' );


// AXIS //

/**
* Axis constructor.
*
* @private
* @constructor
* @param {Options} options - axis options
* @param {Function} options.scale - scale function
* @param {string} options.orientation - axis orientation
* @param {(Array|null)} [options.ticks] - tick values
* @param {NonNegativeInteger} [options.numTicks] - number of ticks
* @param {(null|string|Function)} [options.tickFormat] - tick format
* @param {NonNegativeInteger} [options.tickSize] - tick size
* @param {NonNegativeInteger} [options.tickSizeInner] - inner tick size
* @param {NonNegativeInteger} [options.tickSizeOuter] - outer tick size
* @param {NonNegativeInteger} [options.tickPadding] - tick padding
* @param {string} [options.engine='svg'] - rendering engine
* @throws {TypeError} must provide valid options
* @returns {Axis} axis instance
*
* @example
* var scale = require( 'd3-scale' ).scaleLinear();
* var axis = new Axis({
*     'scale': scale,
*     'orientation': 'bottom'
* });
*/
function Axis( options ) {
	var self;
	var opts;
	var err;
	if ( !( this instanceof Axis ) ) {
		return new Axis( options );
	}
	self = this;
	opts = copy( defaults );
	err = validate( opts, options );
	if ( err ) {
		throw err;
	}
	debug( 'Creating an axis with the following configuration: %s.', JSON.stringify( opts ) );
	EventEmitter.call( this );

	Object.defineProperty( this, '_scale', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.scale
	});
	Object.defineProperty( this, '_orientation', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.orientation
	});
	Object.defineProperty( this, '_ticks', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.ticks
	});
	Object.defineProperty( this, '_numTicks', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.numTicks
	});
	Object.defineProperty( this, '_tickFormat', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.tickFormat
	});
	Object.defineProperty( this, '_tickSize', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.tickSize
	});
	Object.defineProperty( this, '_tickSizeInner', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.tickSizeInner
	});
	Object.defineProperty( this, '_tickSizeOuter', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.tickSizeOuter
	});
	Object.defineProperty( this, '_tickPadding', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.tickPadding
	});
	Object.defineProperty( this, '_engine', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': opts.engine
	});
	Object.defineProperty( this, '__engine__', {
		'configurable': false,
		'enumerable': false,
		'writable': true,
		'value': null
	});

	// Initialize a plot engine:
	this.__engine__ = createEngine( this, {
		'engine': this._engine // TODO: options?
	});

	// Listen for 'render' events:
	this.__engine__.on( 'render', onRender );

	return this;

	/**
	* Re-emits a render event.
	*
	* @private
	*/
	function onRender() {
		var args;
		var i;
		args = new Array( arguments.length+1 );
		args[ 0 ] = 'render';
		for ( i = 0; i < arguments.length; i++ ) {
			args[ i+1 ] = arguments[ i ];
		}
		self.emit.apply( self, args );
	}
} // end FUNCTION Axis()

/*
* Create a prototype which inherits from the parent prototype.
*/
Axis.prototype = Object.create( EventEmitter.prototype );

/*
* Set the constructor.
*/
Axis.prototype.constructor = Axis;

/**
* Scale function.
*
* @memberof Axis.prototype
* @name scale
* @type {Function}
* @throws {TypeError} must be a function
*
* @example
* var scale = require( 'd3-scale' );
* var axis = new Axis({
*     'scale': scale.scaleLinear(),
*     'orientation': 'top'
* });
* axis.scale = scale.scaleTime();
*
* var f = axis.scale;
* // returns <Function>
*/
Object.defineProperty( Axis.prototype, 'scale', {
	'configurable': false,
	'enumerable': true,
	'set': require( './props/scale/set.js' ),
	'get': require( './props/scale/get.js' )
});

