'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:main' );
var h = require( 'virtual-dom/h' );
var domain = require( './domain.js' );
var ticks = require( './ticks.js' );


// VARIABLES //

var ELEMENT = 'g';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'property': 'axis',
	'className': 'axis',
	'attributes': {
		'fill': 'none',
		'font-size': null,
		'font-family': null,
		'text-anchor': null
	}
};


// RENDER //

/**
* Axis component returned as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var children;

	PROPS.attributes[ 'text-anchor' ] = ctx.textAnchor();
	PROPS.attributes[ 'font-size' ] = 10; // TODO: option
	PROPS.attributes[ 'font-family' ] = 'sans-serif'; // TODO: option

	debug( 'Rendering tick marks...' );
	children = ticks( ctx );

	debug( 'Rendering domain line...' );
	children.unshift( domain( ctx ) );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
