'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:label' );
var h = require( 'virtual-dom/h' );


// VARIABLES //

var ELEMENT = 'text';


// RENDER //

/**
* Renders an axis label.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var label;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'axis.label',
		'className': 'label noselect',
		'attributes': {
			'stroke': '#000',
			'text-anchor': 'middle',
			'x': ctx.state.graphWidth / 2,
			'y': 45 // FIXME: dependent on orientation
		}
	};

	// TODO: move to `plot/axis`

	label = ctx.state.label;
	debug( 'Axis label: %s.', label );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, label );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
