'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:line' );
var h = require( 'virtual-dom/h' );
var copy = require( '@stdlib/utils/copy' );


// VARIABLES //

var ELEMENT = 'line';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'attributes': {
		'stroke': '#000'
	}
};
var CHILDREN = [];


// RENDER //

/**
* Renders a tick line as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var props;
	var dir;
	var x;
	var y;

	props = copy( PROPS );

	dir = ctx.tickDir();
	debug( 'Tick direction: %d.', dir );

	x = ctx.xAttr();
	y = ctx.yAttr();

	props.attributes[ x+'2' ] = dir * ctx.state.tickSizeInner;
	props.attributes[ y+'1' ] = 0.5;
	props.attributes[ y+'2' ] = 0.5;

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, CHILDREN );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
