'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:line' );
var h = require( 'virtual-dom/h' );


// LINE //

/**
* Renders a tick line as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function line( ctx ) {
	var opts;
	var dir;
	var x;
	var y;

	dir = ctx.tickDir();
	debug( 'Tick direction: %d.', dir );

	x = ctx.xAttr();
	y = ctx.yAttr();

	opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'attributes': {
			'stroke': '#000'
		}
	};
	opts.attributes[ x+'2' ] = dir * ctx.state.tickSizeInner;
	opts.attributes[ y+'1' ] = 0.5;
	opts.attributes[ y+'2' ] = 0.5;

	debug( 'Generating a virtual DOM tree with the following configuration: %s.', JSON.stringify( opts ) );

	return h( 'line', opts, [] );
} // end FUNCTION line()


// EXPORTS //

module.exports = line;
